INSERT INTO `menus` VALUES (1,'MENU_ITEM_DROP',100,100,'YES','Home','home.php','','LEFT','2012-05-30'),(2,'MENU_ITEM',450,400,'YES','How to find us','googlemaps.php','<br>\r\n\r\n<h2>How To Find Us</h2>\r\n\r\n\r\n\r\n\r\n','RIGHT','2012-06-12'),(68,'MENU_ITEM',400,200,'NO','Christmas Services and and List of Help Needed','','<!-- dispforthevent.php -->\r\n\r\n\r\n<br />\r\n\r\n<h2>Christmas Services 2011</h2>\r\n\r\n<p>\r\n<b>\r\nSunday 18th December\r\n</b>\r\n<br />\r\n10:45 am All Age Carol Service.\r\n<br />\r\n6:30 pm Carols by Candlelight.\r\n</p>\r\n\r\n<p>\r\n<b>\r\nFriday 23th December\r\n</b>\r\n<br />\r\n12:30 pm Open Door Carol Service with Mince Pies and Mulled Wine.\r\n</p>\r\n\r\n<p>\r\n<b>\r\nChristmas Eve Saturday 24th December\r\n</b>\r\n<br />\r\n11:30 pm Midnight Communion.\r\n</p>\r\n\r\n<p>\r\n<b>\r\nChristmas DaySunday 25th December\r\n</b>\r\n<br />\r\n9.00 am Holy Communion.\r\n<br />\r\n10.45 am Christmas Family Hour.\r\n</p>\r\n\r\n<br />\r\n<p>\r\n<b>\r\nSimon\\\'s Help Needed for Christmas List\r\n</b>\r\n</p>\r\n\r\n<p>\r\n1) Leaflets\r\n<br />\r\na) For Rufford estate. Meet Sun 11th 2-30 pm by the shops in the centre to take leaflets to houses. \r\n<br />\r\nb) Lye High Street  Mon 12th at 12-00 noon or Tues 13th at 6-00pm\r\n<br />\r\n<br />\r\n2) Decorating church. On Friday 16th from 12-30. The big tree will be arriving at about 2-00pm. People needed to help put it up etc.  Lots to do.\r\n<br />\r\n<br />\r\n3) Drama - anyone interested in some sort of drama for carols by candlight on Sun 18th?\r\n<br />\r\n<br />\r\n4)  Carol singing  at the Windsor Castle. We must have been tuneful - they have invited us back.  Sat 17th at 7-00pm or Fri 23rd 7-00pm. I will be there but am not much of a singer. To make sure we can make a reasonable sound please sign up on Sunday so I can know if we will have numbers.\r\n<br />\r\n<br />\r\n5) Midnight Communion. Christmas Eve.  Any volunteers for PA, readings, serving communion, acting as bouncers